        <Text style={styles.text}>Welcome to my blog</Text>
        <Text> {blogPost.title} </Text>
        <FlatList
         data={blogPost}
         keyExtractor={(item) => item}
         renderItem={({item})=> {
             return <Text>{item.title}</Text>
         }}

        />
    </View>);
}


const styles = StyleSheet.create({
    container: {
        flex: 1,
        margin: 10
    },
    text: {
        fontSize: 20
    }
});

export default IndexScreen;
